Row.sql:
CREATE OR ALTER FUNCTION GetParallelPacemakerDivisor ( @equipmentuid BIGINT, @joborderuid BIGINT, @isWorkUnitOnly BIT ) RETURNS TABLE AS RETURN WITH totalduration AS ( SELECT POWER(SUM(POWER(A.duration,-1)),-1) as duration, @equipmentuid as modeluid FROM ( SELECT wd.uid, wmms.modeluid, wd.duration FROM workdefinition wd JOIN workmastermodelspecification wmms ON wd.workmasteruid = wmms.workmasteruid JOIN model m ON wmms.modeluid = m.uid WHERE m.ispacemaker = 1 AND wd.uid IN ( SELECT childuid FROM workdefinitionlink WHERE parentuid IN ( SELECT workdefinitionuid FROM joborderworkdefinitionlink WHERE joborderuid = @joborderuid ) ) ) A ), pacemakerduration AS ( SELECT A.duration as duration, @equipmentuid as modeluid FROM ( SELECT wd.uid, wmms.modeluid, wd.duration FROM workdefinition wd JOIN workmastermodelspecification wmms ON wd.workmasteruid = wmms.workmasteruid JOIN model m ON wmms.modeluid = m.uid WHERE wd.uid IN ( SELECT childuid FROM workdefinitionlink WHERE parentuid IN ( SELECT workdefinitionuid FROM joborderworkdefinitionlink WHERE joborderuid = @joborderuid ) ) ) A WHERE A.modeluid = @equipmentuid ), pacemakers AS ( SELECT COUNT(ml2.childuid) as pacemakercount, @equipmentuid as modeluid FROM modellink ml2 JOIN model m ON ml2.childuid = m.uid WHERE parentuid IN (SELECT parentuid FROM modellink ml WHERE ml.childuid = @equipmentuid) AND m.ispacemaker = 1
)
SELECT (CASE WHEN @isWorkUnitOnly = 1 THEN @isWorkUnitOnly ELSE IIF(@joborderuid IS NOT NULL, pd.duration/td.duration, pm.pacemakercount) END) AS divisor FROM pacemakers pm LEFT JOIN pacemakerduration pd ON pd.modeluid = pm.modeluid LEFT JOIN totalduration td ON pm.modeluid = td.modeluid

CREATE OR ALTER PROCEDURE GetProductionEventDurations( @timeperiods timeperiodtype READONLY, @parent_children uidmap READONLY, @shiftinstanceuid BIGINT, @materialmasteruid BIGINT) AS DECLARE @isWorkUnitOnly BIT = CASE WHEN 6 IN (SELECT m.modeltypeuid FROM @parent_children ids JOIN model m ON m.uid = ids.fromuid) THEN 1 ELSE 0 END DECLARE @lastAggregatedTime DATETIMEOFFSET; SELECT @lastAggregatedTime = DATEADD(HOUR, 1, MAX(eventdatetime)) FROM aggregatedproductionevent;
DECLARE @historicaltimeperiods timeperiodtype; DECLARE @recenttimeperiods timeperiodtype;
IF @lastAggregatedTime IS NOT NULL BEGIN INSERT INTO @historicaltimeperiods(id, starttime, endtime) SELECT tp.id, tp.starttime, IIF(tp.endtime < @lastAggregatedTime, tp.endtime, @lastAggregatedTime) FROM @timeperiods AS tp WHERE tp.starttime <= @lastAggregatedTime;
INSERT INTO @recenttimeperiods(id, starttime, endtime) SELECT tp.id, IIF(tp.starttime < @lastAggregatedTime, @lastAggregatedTime, tp.starttime), tp.endtime FROM @timeperiods AS tp WHERE @lastAggregatedTime < tp.endtime; END ELSE INSERT INTO @recenttimeperiods(id, starttime, endtime) SELECT tp.id, tp.starttime, tp.endtime FROM @timeperiods AS tp;
DROP TABLE IF EXISTS #totalProductionQty CREATE TABLE #totalProductionQty( modeluid BIGINT INDEX IX1 CLUSTERED, timeperiodid BIGINT INDEX IX2 NONCLUSTERED, eventtypeuid BIGINT, eventcategoryuid BIGINT INDEX IX3 NONCLUSTERED, duration NUMERIC(38, 5), quantity NUMERIC(38, 5) )
IF EXISTS (SELECT TOP(1) id FROM @historicaltimeperiods) BEGIN INSERT INTO #totalProductionQty(modeluid, timeperiodid, eventtypeuid, eventcategoryuid, duration, quantity) SELECT pc.fromuid, tp.id AS timeperiodid, ape.eventtypeuid, ape.eventcategoryuid, SUM(quantitytime/ppd.divisor) AS duration, SUM(quantity) AS quantity FROM aggregatedproductionevent ape INNER JOIN @parent_children pc ON ape.modeluid = pc.touid INNER JOIN @historicaltimeperiods tp ON (ape.eventdatetime >= FORMAT(tp.starttime, 'yyyy-MM-dd HH:00:00') AND ape.eventdatetime < FORMAT(tp.endtime, 'yyyy-MM-dd HH:00:00')) CROSS APPLY GetParallelPacemakerDivisor(ape.modeluid, ape.joborderuid, @isWorkUnitOnly) AS ppd WHERE ((@shiftinstanceuid IS NULL) OR (@shiftinstanceuid = ape.shiftinstanceuid)) AND ((@materialmasteruid IS NULL) OR (@materialmasteruid = ape.materialmasteruid)) GROUP BY pc.fromuid, tp.id, ape.eventtypeuid, ape.eventcategoryuid END
IF EXISTS (SELECT TOP(1) id FROM @recenttimeperiods) BEGIN INSERT INTO #totalProductionQty(modeluid, timeperiodid, eventtypeuid, eventcategoryuid, duration, quantity) SELECT pc.fromuid, tp.id AS timeperiodid, pe.eventtypeuid, pe.eventcategoryuid, SUM(COALESCE(pe.quantity * pe.idealcycletime, 0)/ppd.divisor) AS duration, SUM(COALESCE(pe.quantity, 0)) AS quantity FROM productionevent pe INNER JOIN @parent_children pc ON pe.modeluid = pc.touid INNER JOIN @recenttimeperiods tp ON (tp.starttime <= pe.eventtime AND pe.eventtime < tp.endtime) CROSS APPLY GetParallelPacemakerDivisor(pe.modeluid, pe.joborderuid, @isWorkUnitOnly) AS ppd WHERE (pe.shiftinstanceuid IS NOT NULL OR pe.joborderuid IS NOT NULL) AND ((@shiftinstanceuid IS NULL) OR (@shiftinstanceuid = pe.shiftinstanceuid)) AND ((@materialmasteruid IS NULL) OR (@materialmasteruid = pe.materialmasteruid)) GROUP BY pc.fromuid, tp.id, pe.eventtypeuid, pe.eventcategoryuid END
SELECT tpq.modeluid AS modeluid, tpq.timeperiodid AS timeperiodid, rt.name AS eventtypename, rc.name AS eventcategoryname, tpq.eventtypeuid AS eventtypeuid, tpq.eventcategoryuid AS eventcategoryuid, SUM(tpq.duration) AS duration, SUM(tpq.quantity) AS quantity FROM #totalProductionQty tpq, reasoncategory rc, reasontype rt WHERE rc.uid = tpq.eventcategoryuid AND rt.uid = rc.reasontypeuid GROUP BY tpq.modeluid, tpq.timeperiodid, rt.name, rc.name, tpq.eventtypeuid, tpq.eventcategoryuid;

